    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Gestiona JMJ{% endblock %}

{% block body %}
    <h3>Proveedores</h3>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Nombre</th>
                <th>Código Postal</th>
                <th>Localidad</th>
                <th>Email</th>
                <th>Teléfono</th>
                <th></th>
                <th></th>
            </tr>
        </thead>
        <tbody>
        {% for proveedor in proveedors %}
            <tr>
                <td>{{ proveedor.nombre }}</td>
                <td>{{ proveedor.codigoPostal }}</td>
                <td>{{ proveedor.localidad }}</td>
                <td>{{ proveedor.email }}</td>
                <td>{{ proveedor.telefono }}</td>
                <td><button class=\"btn\" onclick=\"window.location.href='{{ path('proveedor_edit', {'id': proveedor.id}) }}'\">Editar</button></td>
                <td>{{ include('proveedor/_delete_form.html.twig') }}</td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"7\">No hay proovedores registrados.</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('proveedor_new') }}\">Añadir proveedor</a>
{% endblock %}
", "proveedor/index.html.twig", "C:\\xampp\\htdocs\\restaurante\\templates\\proveedor\\index.html.twig");
